import React, { useState, useRef, useEffect } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import { Send, Download, Mail, User, Bot, Loader } from 'lucide-react';

const ChatPage = () => {
  const { language } = useLanguage();
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [sessionData, setSessionData] = useState({
    email: '',
    caseNumber: '',
    summary: ''
  });
  const [showSessionForm, setShowSessionForm] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async () => {
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      text: inputMessage,
      sender: 'user',
      timestamp: new Date().toISOString()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      const response = await fetch('http://localhost:5000/api/ask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          question: inputMessage,
          language: language,
          history: messages.map(m => `${m.sender}: ${m.text}`).join('\n')
        })
      });

      const data = await response.json();

      if (response.ok) {
        const botMessage = {
          id: Date.now() + 1,
          text: data.answer,
          sender: 'bot',
          timestamp: new Date().toISOString(),
          documents: data.documents || []
        };

        setMessages(prev => [...prev, botMessage]);
      } else {
        const errorMessage = {
          id: Date.now() + 1,
          text: language === 'en' 
            ? 'Sorry, I encountered an error. Please try again or contact court staff for assistance.'
            : 'Lo siento, encontré un error. Por favor inténtalo de nuevo o contacta al personal del tribunal para obtener ayuda.',
          sender: 'bot',
          timestamp: new Date().toISOString()
        };
        setMessages(prev => [...prev, errorMessage]);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = {
        id: Date.now() + 1,
        text: language === 'en' 
          ? 'Sorry, I cannot connect to the server. Please check your internet connection and try again.'
          : 'Lo siento, no puedo conectarme al servidor. Por favor verifica tu conexión a internet e inténtalo de nuevo.',
        sender: 'bot',
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const submitSession = async () => {
    if (!sessionData.email || !sessionData.summary) {
      alert(language === 'en' ? 'Please fill in all required fields.' : 'Por favor completa todos los campos requeridos.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/submit-session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: sessionData.email,
          case_number: sessionData.caseNumber,
          summary: sessionData.summary,
          language: language,
          documents: messages
            .filter(m => m.documents)
            .flatMap(m => m.documents)
        })
      });

      if (response.ok) {
        alert(language === 'en' 
          ? 'Session summary sent successfully! Check your email for details.'
          : '¡Resumen de sesión enviado exitosamente! Revisa tu correo electrónico para más detalles.'
        );
        setShowSessionForm(false);
        setSessionData({ email: '', caseNumber: '', summary: '' });
      } else {
        alert(language === 'en' 
          ? 'Error sending session summary. Please try again.'
          : 'Error al enviar el resumen de sesión. Por favor inténtalo de nuevo.'
        );
      }
    } catch (error) {
      console.error('Error submitting session:', error);
      alert(language === 'en' 
        ? 'Error submitting session. Please try again.'
        : 'Error al enviar la sesión. Por favor inténtalo de nuevo.'
      );
    }
  };

  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString([], { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-4xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            {language === 'en' ? 'Legal Assistant Chat' : 'Chat del Asistente Legal'}
          </h1>
          <p className="text-gray-600">
            {language === 'en' 
              ? 'Ask questions about court procedures, forms, and legal processes. This is for informational purposes only and does not constitute legal advice.'
              : 'Haz preguntas sobre procedimientos judiciales, formularios y procesos legales. Esto es solo para fines informativos y no constituye asesoramiento legal.'
            }
          </p>
        </div>

        {/* Chat Container */}
        <div className="bg-white rounded-lg shadow-md h-96 flex flex-col">
          {/* Messages */}
          <div className="flex-1 overflow-y-auto p-6 space-y-4">
            {messages.length === 0 && (
              <div className="text-center text-gray-500 py-8">
                <Bot className="w-12 h-12 mx-auto mb-4 text-gray-400" />
                <p>
                  {language === 'en' 
                    ? 'Start a conversation by typing your question below.'
                    : 'Inicia una conversación escribiendo tu pregunta abajo.'
                  }
                </p>
              </div>
            )}

            {messages.map((message) => (
              <div
                key={message.id}
                className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
              >
                <div
                  className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                    message.sender === 'user'
                      ? 'bg-blue-600 text-white'
                      : 'bg-gray-100 text-gray-900'
                  }`}
                >
                  <div className="flex items-center mb-1">
                    {message.sender === 'user' ? (
                      <User className="w-4 h-4 mr-2" />
                    ) : (
                      <Bot className="w-4 h-4 mr-2" />
                    )}
                    <span className="text-xs opacity-75">
                      {formatTime(message.timestamp)}
                    </span>
                  </div>
                  <p className="text-sm">{message.text}</p>

                  {message.documents && message.documents.length > 0 && (
                    <div className="mt-3 pt-3 border-t border-gray-200">
                      <p className="text-xs font-semibold mb-2">
                        {language === 'en' ? 'Suggested Documents:' : 'Documentos Sugeridos:'}
                      </p>
                      <div className="space-y-1">
                        {message.documents.map((doc, index) => (
                          <div key={index} className="text-xs flex items-center">
                            <Download className="w-3 h-3 mr-1" />
                            {doc}
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </div>
              </div>
            ))}

            {isLoading && (
              <div className="flex justify-start">
                <div className="bg-gray-100 text-gray-900 max-w-xs lg:max-w-md px-4 py-2 rounded-lg">
                  <div className="flex items-center">
                    <Bot className="w-4 h-4 mr-2" />
                    <Loader className="w-4 h-4 animate-spin" />
                    <span className="ml-2 text-sm">
                      {language === 'en' ? 'Thinking...' : 'Pensando...'}
                    </span>
                  </div>
                </div>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Input */}
          <div className="border-t border-gray-200 p-4">
            <div className="flex space-x-2">
              <textarea
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder={language === 'en' 
                  ? 'Type your question here...' 
                  : 'Escribe tu pregunta aquí...'
                }
                className="flex-1 border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                rows="2"
                disabled={isLoading}
              />
              <button
                onClick={sendMessage}
                disabled={!inputMessage.trim() || isLoading}
                className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
              >
                <Send className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>

        {/* Session Summary */}
        {messages.length > 0 && (
          <div className="mt-6 bg-white rounded-lg shadow-md p-6">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold text-gray-900">
                {language === 'en' ? 'Session Summary' : 'Resumen de Sesión'}
              </h3>
              <button
                onClick={() => setShowSessionForm(!showSessionForm)}
                className="flex items-center text-blue-600 hover:text-blue-700 transition-colors"
              >
                <Mail className="w-4 h-4 mr-2" />
                {language === 'en' ? 'Email Summary' : 'Enviar Resumen'}
              </button>
            </div>

            {showSessionForm && (
              <div className="space-y-4 p-4 bg-gray-50 rounded-lg">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    {language === 'en' ? 'Email Address *' : 'Dirección de Correo *'}
                  </label>
                  <input
                    type="email"
                    value={sessionData.email}
                    onChange={(e) => setSessionData(prev => ({ ...prev, email: e.target.value }))}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="your@email.com"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    {language === 'en' ? 'Case Number (Optional)' : 'Número de Caso (Opcional)'}
                  </label>
                  <input
                    type="text"
                    value={sessionData.caseNumber}
                    onChange={(e) => setSessionData(prev => ({ ...prev, caseNumber: e.target.value }))}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="ABC123456"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    {language === 'en' ? 'Summary *' : 'Resumen *'}
                  </label>
                  <textarea
                    value={sessionData.summary}
                    onChange={(e) => setSessionData(prev => ({ ...prev, summary: e.target.value }))}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none"
                    rows="3"
                    placeholder={language === 'en' 
                      ? 'Brief summary of your legal questions...' 
                      : 'Breve resumen de tus preguntas legales...'
                    }
                  />
                </div>
                <div className="flex space-x-2">
                  <button
                    onClick={submitSession}
                    className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                  >
                    {language === 'en' ? 'Send Summary' : 'Enviar Resumen'}
                  </button>
                  <button
                    onClick={() => setShowSessionForm(false)}
                    className="border border-gray-300 text-gray-700 px-4 py-2 rounded-lg hover:bg-gray-50 transition-colors"
                  >
                    {language === 'en' ? 'Cancel' : 'Cancelar'}
                  </button>
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatPage;
