import React, { useState, useEffect } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import { ArrowLeft, ArrowRight, RotateCcw, Download, FileText, Info } from 'lucide-react';

const FlowchartPage = () => {
  const { language } = useLanguage();
  const [flowchartData, setFlowchartData] = useState(null);
  const [currentStep, setCurrentStep] = useState('start');
  const [progress, setProgress] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchFlowchart();
  }, []);

  const fetchFlowchart = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/flowchart');
      if (response.ok) {
        const data = await response.json();
        setFlowchartData(data);
      } else {
        console.error('Failed to fetch flowchart');
      }
    } catch (error) {
      console.error('Error fetching flowchart:', error);
    } finally {
      setLoading(false);
    }
  };

  const currentStepData = flowchartData?.flowchart?.[currentStep];

  const handleOptionClick = (option) => {
    setProgress([...progress, { step: currentStep, option: option.text[language] }]);
    setCurrentStep(option.next);
  };

  const handleBack = () => {
    if (progress.length > 0) {
      const newProgress = progress.slice(0, -1);
      setProgress(newProgress);
      setCurrentStep(newProgress.length > 0 ? newProgress[newProgress.length - 1].step : 'start');
    }
  };

  const handleReset = () => {
    setCurrentStep('start');
    setProgress([]);
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'A': return '#dc3545'; // Red for DV
      case 'B': return '#fd7e14'; // Orange for Custody/Support
      case 'C': return '#ffc107'; // Yellow for Divorce
      case 'D': return '#0d6efd'; // Blue for Other
      default: return '#6c757d';
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">
            {language === 'en' ? 'Loading flowchart...' : 'Cargando diagrama...'}
          </p>
        </div>
      </div>
    );
  }

  if (!flowchartData || !currentStepData) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <Info className="w-16 h-16 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-gray-900 mb-2">
            {language === 'en' ? 'Flowchart Not Available' : 'Diagrama No Disponible'}
          </h3>
          <p className="text-gray-600">
            {language === 'en' 
              ? 'The flowchart data could not be loaded. Please try again later.'
              : 'Los datos del diagrama no pudieron cargarse. Por favor inténtalo más tarde.'
            }
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-6xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            {language === 'en' ? 'Legal Process Flowchart' : 'Diagrama del Proceso Legal'}
          </h1>
          <p className="text-gray-600">
            {language === 'en' 
              ? 'Interactive step-by-step guidance through legal processes. Follow the flowchart to understand your options and next steps.'
              : 'Guía interactiva paso a paso a través de procesos legales. Sigue el diagrama para entender tus opciones y próximos pasos.'
            }
          </p>
        </div>

        {/* Progress Bar */}
        {progress.length > 0 && (
          <div className="bg-white rounded-lg shadow-md p-4 mb-6">
            <div className="flex items-center justify-between mb-2">
              <h3 className="font-semibold text-gray-900">
                {language === 'en' ? 'Your Path' : 'Tu Camino'}
              </h3>
              <button
                onClick={handleReset}
                className="flex items-center text-blue-600 hover:text-blue-700 transition-colors"
              >
                <RotateCcw className="w-4 h-4 mr-2" />
                {language === 'en' ? 'Start Over' : 'Comenzar de Nuevo'}
              </button>
            </div>
            <div className="flex items-center space-x-2 overflow-x-auto">
              {progress.map((item, index) => (
                <div key={index} className="flex items-center">
                  <div className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm whitespace-nowrap">
                    {item.option}
                  </div>
                  {index < progress.length - 1 && (
                    <ArrowRight className="w-4 h-4 text-gray-400 mx-2" />
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Current Step */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="flex items-start justify-between mb-4">
            <div className="flex-1">
              <h2 className="text-2xl font-bold text-gray-900 mb-2">
                {currentStepData.title[language]}
              </h2>
              {currentStepData.priority && (
                <div 
                  className="inline-block px-3 py-1 rounded-full text-white text-sm font-medium mb-3"
                  style={{ backgroundColor: getPriorityColor(currentStepData.priority) }}
                >
                  {language === 'en' ? `Priority ${currentStepData.priority}` : `Prioridad ${currentStepData.priority}`}
                </div>
              )}
            </div>
          </div>

          <p className="text-gray-700 text-lg mb-6">
            {currentStepData.description[language]}
          </p>

          {/* Options */}
          {currentStepData.options && (
            <div className="space-y-3 mb-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-3">
                {language === 'en' ? 'Choose an option:' : 'Elige una opción:'}
              </h3>
              {currentStepData.options.map((option) => (
                <button
                  key={option.id}
                  onClick={() => handleOptionClick(option)}
                  className="w-full text-left p-4 border border-gray-200 rounded-lg hover:border-blue-300 hover:bg-blue-50 transition-colors group"
                  style={{ 
                    borderLeftColor: getPriorityColor(option.priority),
                    borderLeftWidth: '8px'
                  }}
                >
                  <div className="flex items-center justify-between">
                    <span className="text-gray-900 font-medium">
                      {option.text[language]}
                    </span>
                    <ArrowRight className="w-5 h-5 text-gray-400 group-hover:text-blue-600 transition-colors" />
                  </div>
                </button>
              ))}
            </div>
          )}

          {/* Forms Section */}
          {currentStepData.forms && (
            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
              <h3 className="text-lg font-semibold text-blue-900 mb-3 flex items-center">
                <FileText className="w-5 h-5 mr-2" />
                {language === 'en' ? 'Required Forms:' : 'Formularios Requeridos:'}
              </h3>
              <div className="space-y-2">
                {currentStepData.forms.map((form, index) => (
                  <div key={index} className="flex items-center justify-between bg-white p-3 rounded border">
                    <div>
                      <strong className="text-gray-900">{form.number}</strong>
                      <span className="text-gray-600 ml-2">- {form.name[language]}</span>
                    </div>
                    <button className="flex items-center text-blue-600 hover:text-blue-700 transition-colors">
                      <Download className="w-4 h-4 mr-1" />
                      {language === 'en' ? 'Download' : 'Descargar'}
                    </button>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Next Steps */}
          {currentStepData.next_steps && (
            <div className="bg-green-50 border border-green-200 rounded-lg p-4">
              <h3 className="text-lg font-semibold text-green-900 mb-3">
                {language === 'en' ? 'Next Steps:' : 'Próximos Pasos:'}
              </h3>
              <ol className="space-y-2">
                {currentStepData.next_steps[language].map((step, index) => (
                  <li key={index} className="flex items-start">
                    <span className="bg-green-600 text-white rounded-full w-6 h-6 flex items-center justify-center text-sm font-bold mr-3 flex-shrink-0">
                      {index + 1}
                    </span>
                    <span className="text-green-800">{step}</span>
                  </li>
                ))}
              </ol>
            </div>
          )}
        </div>

        {/* Navigation */}
        <div className="flex justify-between">
          {progress.length > 0 && (
            <button
              onClick={handleBack}
              className="flex items-center px-6 py-3 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
            >
              <ArrowLeft className="w-4 h-4 mr-2" />
              {language === 'en' ? 'Back' : 'Atrás'}
            </button>
          )}
          <button
            onClick={handleReset}
            className="flex items-center px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
          >
            <RotateCcw className="w-4 h-4 mr-2" />
            {language === 'en' ? 'Start Over' : 'Comenzar de Nuevo'}
          </button>
        </div>

        {/* Help Section */}
        <div className="mt-8 bg-yellow-50 border border-yellow-200 rounded-lg p-6">
          <h3 className="text-lg font-semibold text-yellow-900 mb-2">
            {language === 'en' ? 'Need Help?' : '¿Necesitas Ayuda?'}
          </h3>
          <p className="text-yellow-800 mb-4">
            {language === 'en' 
              ? 'This flowchart provides general guidance only. For specific legal advice, please consult with an attorney or visit the court self-help center.'
              : 'Este diagrama proporciona solo orientación general. Para asesoramiento legal específico, por favor consulta con un abogado o visita el centro de autoayuda del tribunal.'
            }
          </p>
          <div className="flex space-x-4">
            <a
              href="/chat"
              className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
            >
              {language === 'en' ? 'Ask AI Assistant' : 'Preguntar al Asistente IA'}
            </a>
            <a
              href="/staff"
              className="border border-blue-600 text-blue-600 px-4 py-2 rounded-lg hover:bg-blue-50 transition-colors"
            >
              {language === 'en' ? 'Contact Staff' : 'Contactar Personal'}
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FlowchartPage;
