import React, { useState, useEffect } from 'react';
import './App.css';
import flowchartData from './flowchart.json';

function App() {
  const [currentStep, setCurrentStep] = useState('start');
  const [language, setLanguage] = useState('en');
  const [progress, setProgress] = useState([]);

  const currentStepData = flowchartData.flowchart[currentStep];

  const handleOptionClick = (option) => {
    setProgress([...progress, { step: currentStep, option: option.text[language] }]);
    setCurrentStep(option.next);
  };

  const handleBack = () => {
    if (progress.length > 0) {
      const newProgress = progress.slice(0, -1);
      setProgress(newProgress);
      setCurrentStep(newProgress.length > 0 ? newProgress[newProgress.length - 1].step : 'start');
    }
  };

  const handleReset = () => {
    setCurrentStep('start');
    setProgress([]);
  };

  const toggleLanguage = () => {
    setLanguage(language === 'en' ? 'es' : 'en');
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'A': return '#dc3545'; // Red for DV
      case 'B': return '#fd7e14'; // Orange for Custody/Support
      case 'C': return '#ffc107'; // Yellow for Divorce
      case 'D': return '#0d6efd'; // Blue for Other
      default: return '#6c757d';
    }
  };

  return (
    <div className="App">
      <header className="header">
        <div className="header-content">
          <h1 className="title">
            {language === 'en' ? 'Family Court Self-Help' : 'Centro de Autoayuda de la Corte Familiar'}
          </h1>
          <button onClick={toggleLanguage} className="language-toggle">
            {language === 'en' ? 'Español' : 'English'}
          </button>
        </div>
      </header>

      <main className="main-content">
        <div className="progress-bar">
          {progress.map((item, index) => (
            <div key={index} className="progress-item">
              <span className="progress-text">{item.option}</span>
              {index < progress.length - 1 && <span className="progress-arrow">→</span>}
            </div>
          ))}
        </div>

        <div className="step-container">
          <div className="step-header">
            <h2 className="step-title">{currentStepData.title[language]}</h2>
            {currentStepData.priority && (
              <div 
                className="priority-badge"
                style={{ backgroundColor: getPriorityColor(currentStepData.priority) }}
              >
                Priority {currentStepData.priority}
              </div>
            )}
          </div>

          <p className="step-description">{currentStepData.description[language]}</p>

          {currentStepData.options && (
            <div className="options-container">
              {currentStepData.options.map((option) => (
                <button
                  key={option.id}
                  onClick={() => handleOptionClick(option)}
                  className="option-button"
                  style={{ 
                    borderLeftColor: getPriorityColor(option.priority),
                    borderLeftWidth: '8px'
                  }}
                >
                  <span className="option-text">{option.text[language]}</span>
                  <span className="option-arrow">→</span>
                </button>
              ))}
            </div>
          )}

          {currentStepData.forms && (
            <div className="forms-section">
              <h3>{language === 'en' ? 'Required Forms:' : 'Formularios Requeridos:'}</h3>
              <div className="forms-list">
                {currentStepData.forms.map((form, index) => (
                  <div key={index} className="form-item">
                    <strong>{form.number}</strong> - {form.name[language]}
                  </div>
                ))}
              </div>
            </div>
          )}

          {currentStepData.next_steps && (
            <div className="next-steps-section">
              <h3>{language === 'en' ? 'Next Steps:' : 'Próximos Pasos:'}</h3>
              <ol className="next-steps-list">
                {currentStepData.next_steps[language].map((step, index) => (
                  <li key={index} className="next-step-item">{step}</li>
                ))}
              </ol>
            </div>
          )}
        </div>

        <div className="navigation-buttons">
          {progress.length > 0 && (
            <button onClick={handleBack} className="back-button">
              {language === 'en' ? '← Back' : '← Atrás'}
            </button>
          )}
          <button onClick={handleReset} className="reset-button">
            {language === 'en' ? 'Start Over' : 'Comenzar de Nuevo'}
          </button>
        </div>
      </main>
    </div>
  );
}

export default App;
