import React, { useState } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import { 
  Shield, 
  Phone, 
  AlertTriangle, 
  Heart, 
  MapPin, 
  ExternalLink, 
  Lock, 
  Eye, 
  EyeOff, 
  Download, 
  FileText, 
  Clock, 
  UserCheck, 
  Info,
  ArrowRight,
  CheckCircle
} from 'lucide-react';

const DVHelperPage = () => {
  const { language } = useLanguage();
  const [showSafetyTips, setShowSafetyTips] = useState(false);
  const [showEmergencyInfo, setShowEmergencyInfo] = useState(true);
  const [currentStep, setCurrentStep] = useState(0);

  const emergencyResources = [
    {
      title: { en: "Immediate Danger", es: "Peligro Inmediato" },
      resources: [
        { name: { en: "Call 911", es: "Llama al 911" }, number: "911", description: { en: "If you're in immediate danger", es: "Si estás en peligro inmediato" } },
        { name: { en: "Emergency Protective Order", es: "Orden de Protección de Emergencia" }, description: { en: "Police can request immediate protection", es: "La policía puede solicitar protección inmediata" } }
      ]
    },
    {
      title: { en: "24/7 Support", es: "Apoyo 24/7" },
      resources: [
        { name: { en: "National Domestic Violence Hotline", es: "Línea Nacional de Violencia Doméstica" }, number: "1-800-799-7233", description: { en: "24/7 confidential support", es: "Apoyo confidencial 24/7" } },
        { name: { en: "San Mateo County Hotline", es: "Línea de San Mateo" }, number: "1-800-300-1080", description: { en: "Local support and resources", es: "Apoyo y recursos locales" } }
      ]
    },
    {
      title: { en: "Local Resources", es: "Recursos Locales" },
      resources: [
        { name: { en: "Court Self-Help Center", es: "Centro de Autoayuda del Tribunal" }, link: "https://www.sanmateocourt.org/self_help", description: { en: "Free legal assistance", es: "Asistencia legal gratuita" } },
        { name: { en: "Domestic Violence Shelters", es: "Refugios de Violencia Doméstica" }, description: { en: "Safe housing and support services", es: "Vivienda segura y servicios de apoyo" } }
      ]
    }
  ];

  const safetyPlanningSteps = [
    {
      phase: { en: "Before Filing", es: "Antes de Presentar" },
      steps: [
        { en: "Gather important documents (ID, birth certificates, financial records)", es: "Reúne documentos importantes (identificación, actas de nacimiento, registros financieros)" },
        { en: "Save money in a separate, secure account", es: "Ahorra dinero en una cuenta separada y segura" },
        { en: "Plan where you can stay safely", es: "Planifica dónde puedes quedarte de forma segura" },
        { en: "Pack an emergency bag with essentials", es: "Empaca una bolsa de emergencia con elementos esenciales" },
        { en: "Memorize important phone numbers", es: "Memoriza números de teléfono importantes" }
      ]
    },
    {
      phase: { en: "During the Process", es: "Durante el Proceso" },
      steps: [
        { en: "Keep copies of all court papers in a safe place", es: "Mantén copias de todos los documentos judiciales en un lugar seguro" },
        { en: "Document any violations or incidents", es: "Documenta cualquier violación o incidente" },
        { en: "Have a safety plan ready at all times", es: "Ten un plan de seguridad listo en todo momento" },
        { en: "Tell trusted friends/family about your situation", es: "Cuéntale a amigos/familiares de confianza sobre tu situación" },
        { en: "Consider changing passwords and phone numbers", es: "Considera cambiar contraseñas y números de teléfono" }
      ]
    },
    {
      phase: { en: "After Filing", es: "Después de Presentar" },
      steps: [
        { en: "Carry your restraining order with you", es: "Lleva tu orden de restricción contigo" },
        { en: "Keep a log of any violations", es: "Mantén un registro de cualquier violación" },
        { en: "Have emergency contacts readily available", es: "Ten contactos de emergencia fácilmente disponibles" },
        { en: "Know your local police department's number", es: "Conoce el número de tu departamento de policía local" },
        { en: "Consider getting a new phone number", es: "Considera obtener un nuevo número de teléfono" }
      ]
    }
  ];

  const quickActions = [
    {
      title: { en: "File Emergency Order", es: "Presentar Orden de Emergencia" },
      description: { en: "Get immediate protection", es: "Obtén protección inmediata" },
      action: { en: "Go to courthouse now", es: "Ve al tribunal ahora" },
      urgent: true
    },
    {
      title: { en: "Contact Police", es: "Contactar Policía" },
      description: { en: "Report violations or threats", es: "Reportar violaciones o amenazas" },
      action: { en: "Call local police", es: "Llama a la policía local" },
      urgent: true
    },
    {
      title: { en: "Find Shelter", es: "Encontrar Refugio" },
      description: { en: "Safe housing options", es: "Opciones de vivienda segura" },
      action: { en: "Contact local shelters", es: "Contacta refugios locales" },
      urgent: false
    },
    {
      title: { en: "Legal Help", es: "Ayuda Legal" },
      description: { en: "Free legal assistance", es: "Asistencia legal gratuita" },
      action: { en: "Visit self-help center", es: "Visita el centro de autoayuda" },
      urgent: false
    }
  ];

  const dvroSteps = [
    { en: "File Request for Domestic Violence Restraining Order (Form DV-100)", es: "Presentar Solicitud de Orden de Restricción por Violencia Doméstica (Formulario DV-100)" },
    { en: "Complete Temporary Restraining Order (Form DV-110)", es: "Completar Orden de Restricción Temporal (Formulario DV-110)" },
    { en: "File with court clerk and pay filing fee (fee waiver available)", es: "Presentar al secretario del tribunal y pagar tarifa de presentación (exención de tarifa disponible)" },
    { en: "Serve papers on the other party (have someone else serve them)", es: "Entregar documentos a la otra parte (hacer que otra persona los entregue)" },
    { en: "Attend court hearing (usually within 21 days)", es: "Asistir a la audiencia judicial (generalmente dentro de 21 días)" },
    { en: "If granted, keep copy of order with you at all times", es: "Si se otorga, mantén una copia de la orden contigo en todo momento" }
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="flex items-center mb-4">
            <Shield className="w-8 h-8 text-red-600 mr-3" />
            <h1 className="text-3xl font-bold text-gray-900">
              {language === 'en' ? 'Domestic Violence Helper' : 'Ayuda para Violencia Doméstica'}
            </h1>
          </div>
          <p className="text-gray-600">
            {language === 'en' 
              ? 'Specialized assistance and resources for domestic violence cases. Your safety is our priority.'
              : 'Asistencia especializada y recursos para casos de violencia doméstica. Tu seguridad es nuestra prioridad.'
            }
          </p>
        </div>

        {/* Emergency Alert */}
        {showEmergencyInfo && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-6 mb-6">
            <div className="flex items-start">
              <AlertTriangle className="w-6 h-6 text-red-600 mr-3 mt-0.5" />
              <div className="flex-1">
                <h3 className="text-lg font-semibold text-red-900 mb-2">
                  {language === 'en' ? 'Emergency Information' : 'Información de Emergencia'}
                </h3>
                <p className="text-red-800 mb-4">
                  {language === 'en' 
                    ? 'If you are in immediate danger, call 911. For confidential support, call the National Domestic Violence Hotline at 1-800-799-7233.'
                    : 'Si estás en peligro inmediato, llama al 911. Para apoyo confidencial, llama a la Línea Nacional de Violencia Doméstica al 1-800-799-7233.'
                  }
                </p>
                <button
                  onClick={() => setShowEmergencyInfo(false)}
                  className="text-red-700 hover:text-red-900 text-sm font-medium"
                >
                  {language === 'en' ? 'Dismiss' : 'Descartar'}
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Quick Actions */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-8">
          {quickActions.map((action, index) => (
            <div
              key={index}
              className={`bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow ${
                action.urgent ? 'border-l-4 border-red-500' : 'border-l-4 border-blue-500'
              }`}
            >
              <h3 className="font-semibold text-gray-900 mb-2">
                {action.title[language]}
              </h3>
              <p className="text-gray-600 text-sm mb-3">
                {action.description[language]}
              </p>
              <button
                className={`w-full px-4 py-2 rounded-lg font-medium transition-colors ${
                  action.urgent
                    ? 'bg-red-600 text-white hover:bg-red-700'
                    : 'bg-blue-600 text-white hover:bg-blue-700'
                }`}
              >
                {action.action[language]}
              </button>
            </div>
          ))}
        </div>

        {/* Emergency Resources */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">
            {language === 'en' ? 'Emergency Resources' : 'Recursos de Emergencia'}
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {emergencyResources.map((section, index) => (
              <div key={index} className="space-y-4">
                <h3 className="font-semibold text-gray-900 text-lg">
                  {section.title[language]}
                </h3>
                <div className="space-y-3">
                  {section.resources.map((resource, resourceIndex) => (
                    <div key={resourceIndex} className="bg-gray-50 rounded-lg p-4">
                      <h4 className="font-medium text-gray-900 mb-1">
                        {resource.name[language]}
                      </h4>
                      {resource.number && (
                        <p className="text-red-600 font-bold text-lg mb-1">
                          {resource.number}
                        </p>
                      )}
                      <p className="text-gray-600 text-sm">
                        {resource.description[language]}
                      </p>
                      {resource.link && (
                        <a
                          href={resource.link}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-blue-600 hover:text-blue-800 text-sm font-medium inline-flex items-center"
                        >
                          {language === 'en' ? 'Visit Website' : 'Visitar Sitio Web'}
                          <ExternalLink className="w-3 h-3 ml-1" />
                        </a>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Safety Planning */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-2xl font-bold text-gray-900">
              {language === 'en' ? 'Safety Planning' : 'Planificación de Seguridad'}
            </h2>
            <button
              onClick={() => setShowSafetyTips(!showSafetyTips)}
              className="flex items-center text-blue-600 hover:text-blue-700 transition-colors"
            >
              {showSafetyTips ? <EyeOff className="w-4 h-4 mr-2" /> : <Eye className="w-4 h-4 mr-2" />}
              {showSafetyTips 
                ? (language === 'en' ? 'Hide Tips' : 'Ocultar Consejos')
                : (language === 'en' ? 'Show Tips' : 'Mostrar Consejos')
              }
            </button>
          </div>

          {showSafetyTips && (
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {safetyPlanningSteps.map((phase, index) => (
                <div key={index} className="space-y-4">
                  <h3 className="font-semibold text-gray-900 text-lg">
                    {phase.phase[language]}
                  </h3>
                  <div className="space-y-2">
                    {phase.steps.map((step, stepIndex) => (
                      <div key={stepIndex} className="flex items-start">
                        <CheckCircle className="w-4 h-4 text-green-600 mr-2 mt-0.5 flex-shrink-0" />
                        <p className="text-gray-700 text-sm">
                          {step[language]}
                        </p>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* DVRO Process */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">
            {language === 'en' ? 'Domestic Violence Restraining Order Process' : 'Proceso de Orden de Restricción por Violencia Doméstica'}
          </h2>
          <div className="space-y-4">
            {dvroSteps.map((step, index) => (
              <div key={index} className="flex items-start">
                <div className="bg-blue-600 text-white rounded-full w-6 h-6 flex items-center justify-center text-sm font-bold mr-3 flex-shrink-0">
                  {index + 1}
                </div>
                <p className="text-gray-700">
                  {step[language]}
                </p>
              </div>
            ))}
          </div>
        </div>

        {/* Important Forms */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">
            {language === 'en' ? 'Important Forms' : 'Formularios Importantes'}
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="border border-gray-200 rounded-lg p-4">
              <h3 className="font-semibold text-gray-900 mb-2">
                {language === 'en' ? 'Request for Domestic Violence Restraining Order' : 'Solicitud de Orden de Restricción por Violencia Doméstica'}
              </h3>
              <p className="text-gray-600 text-sm mb-3">
                {language === 'en' ? 'Form DV-100 - Main application form' : 'Formulario DV-100 - Formulario de solicitud principal'}
              </p>
              <button className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors">
                <Download className="w-4 h-4 mr-2 inline" />
                {language === 'en' ? 'Download Form' : 'Descargar Formulario'}
              </button>
            </div>
            <div className="border border-gray-200 rounded-lg p-4">
              <h3 className="font-semibold text-gray-900 mb-2">
                {language === 'en' ? 'Temporary Restraining Order' : 'Orden de Restricción Temporal'}
              </h3>
              <p className="text-gray-600 text-sm mb-3">
                {language === 'en' ? 'Form DV-110 - For immediate protection' : 'Formulario DV-110 - Para protección inmediata'}
              </p>
              <button className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors">
                <Download className="w-4 h-4 mr-2 inline" />
                {language === 'en' ? 'Download Form' : 'Descargar Formulario'}
              </button>
            </div>
          </div>
        </div>

        {/* Important Notice */}
        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-6">
          <h3 className="text-lg font-semibold text-yellow-900 mb-2">
            {language === 'en' ? 'Important Notice' : 'Aviso Importante'}
          </h3>
          <p className="text-yellow-800">
            {language === 'en' 
              ? 'This information is provided for educational purposes only. For legal advice specific to your situation, please consult with an attorney. If you are in immediate danger, call 911.'
              : 'Esta información se proporciona únicamente con fines educativos. Para asesoramiento legal específico a tu situación, por favor consulta con un abogado. Si estás en peligro inmediato, llama al 911.'
            }
          </p>
        </div>
      </div>
    </div>
  );
};

export default DVHelperPage;
