import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useLanguage } from '../contexts/LanguageContext';
import { 
  Home, 
  MessageCircle, 
  FileText, 
  Users, 
  Shield, 
  Workflow,
  Globe
} from 'lucide-react';

const NavBar = ({ currentUser, setCurrentUser }) => {
  const { language, toggleLanguage } = useLanguage();
  const location = useLocation();

  const navItems = [
    { path: '/', label: { en: 'Home', es: 'Inicio' }, icon: Home },
    { path: '/chat', label: { en: 'Chat', es: 'Chat' }, icon: MessageCircle },
    { path: '/forms', label: { en: 'Forms', es: 'Formularios' }, icon: FileText },
    { path: '/staff', label: { en: 'Staff', es: 'Personal' }, icon: Users },
    { path: '/dv-helper', label: { en: 'DV Helper', es: 'Ayuda DV' }, icon: Shield },
    { path: '/flowchart', label: { en: 'Flowchart', es: 'Diagrama' }, icon: Workflow },
  ];

  const isActive = (path) => location.pathname === path;

  return (
    <nav className="bg-white shadow-lg border-b">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex items-center">
            <div className="flex-shrink-0">
              <h1 className="text-xl font-bold text-gray-900">
                {language === 'en' ? 'Court Kiosk' : 'Quiosco Judicial'}
              </h1>
            </div>
          </div>

          <div className="flex items-center space-x-4">
            <div className="hidden md:block">
              <div className="flex items-center space-x-4">
                {navItems.map((item) => {
                  const Icon = item.icon;
                  return (
                    <Link
                      key={item.path}
                      to={item.path}
                      className={`flex items-center px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                        isActive(item.path)
                          ? 'bg-blue-100 text-blue-700'
                          : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                      }`}
                    >
                      <Icon className="w-4 h-4 mr-2" />
                      {item.label[language]}
                    </Link>
                  );
                })}
              </div>
            </div>

            <button
              onClick={toggleLanguage}
              className="flex items-center px-3 py-2 rounded-md text-sm font-medium text-gray-600 hover:text-gray-900 hover:bg-gray-50 transition-colors"
            >
              <Globe className="w-4 h-4 mr-2" />
              {language === 'en' ? 'Español' : 'English'}
            </button>
          </div>
        </div>

        {/* Mobile menu */}
        <div className="md:hidden">
          <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
            {navItems.map((item) => {
              const Icon = item.icon;
              return (
                <Link
                  key={item.path}
                  to={item.path}
                  className={`flex items-center px-3 py-2 rounded-md text-base font-medium transition-colors ${
                    isActive(item.path)
                      ? 'bg-blue-100 text-blue-700'
                      : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                  }`}
                >
                  <Icon className="w-4 h-4 mr-2" />
                  {item.label[language]}
                </Link>
              );
            })}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default NavBar;
