import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { LocationContext } from '../contexts/LocationContext';
import { topics } from '../data/flows';
import { 
  ChevronLeft, 
  ChevronRight, 
  Home, 
  Users, 
  Shield, 
  DollarSign, 
  CheckCircle,
  AlertCircle,
  ArrowRight,
  Scale,
  Clock
} from 'lucide-react';

const QnA = () => {
  const { topicId } = useParams();
  const navigate = useNavigate();
  const { currentLocation } = useContext(LocationContext);
  const [currentStep, setCurrentStep] = useState(0);
  const [answers, setAnswers] = useState({});
  const [showConfirmation, setShowConfirmation] = useState(false);

  // Get topic data from centralized flows
  const currentTopic = topics[topicId];
  const currentQuestion = currentTopic?.questions[currentStep];

  // Icon mapping for dynamic icon rendering
  const iconMap = {
    Users: Users,
    Shield: Shield,
    DollarSign: DollarSign
  };

  useEffect(() => {
    if (!currentTopic) {
      navigate('/');
    }
  }, [currentTopic, navigate]);

  const handleAnswer = (value) => {
    const newAnswers = { ...answers, [currentQuestion.id]: value };
    setAnswers(newAnswers);

    // Check if we need to show confirmation for urgent situations
    if (currentQuestion.urgent && value === 'yes') {
      setShowConfirmation(true);
      return;
    }

    // Find next question that should be shown
    const nextStep = findNextQuestion(currentStep + 1, newAnswers);

    if (nextStep < currentTopic.questions.length) {
      setCurrentStep(nextStep);
    } else {
      // All questions completed, go to results
      navigate('/results', { 
        state: { 
          topic: topicId, 
          answers: newAnswers,
          topicTitle: currentTopic.title
        } 
      });
    }
  };

  const findNextQuestion = (startIndex, currentAnswers) => {
    for (let i = startIndex; i < currentTopic.questions.length; i++) {
      const question = currentTopic.questions[i];
      if (!question.condition || question.condition(currentAnswers)) {
        return i;
      }
    }
    return currentTopic.questions.length; // No more questions
  };

  const handleBack = () => {
    if (currentStep > 0) {
      // Find previous question that should be shown
      const prevStep = findPreviousQuestion(currentStep - 1, answers);
      setCurrentStep(prevStep);
    } else {
      navigate('/');
    }
  };

  const findPreviousQuestion = (startIndex, currentAnswers) => {
    for (let i = startIndex; i >= 0; i--) {
      const question = currentTopic.questions[i];
      if (!question.condition || question.condition(currentAnswers)) {
        return i;
      }
    }
    return 0; // First question
  };

  const getProgress = () => {
    const totalQuestions = currentTopic.questions.filter(q => 
      !q.condition || q.condition(answers)
    ).length;
    const completedQuestions = Object.keys(answers).length;
    return (completedQuestions / totalQuestions) * 100;
  };

  if (!currentTopic) {
    return null;
  }

  // Dynamically render the icon
  const IconComponent = iconMap[currentTopic.icon];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 py-10">
      <div className="container mx-auto px-4">
        <div className="max-w-4xl mx-auto">
          {/* Emergency Confirmation Modal */}
          {showConfirmation && (
            <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50 p-4 backdrop-blur-sm">
              <div className="bg-white rounded-3xl p-10 max-w-lg w-full shadow-2xl border border-red-200">
                <div className="flex items-center mb-8">
                  <div className="w-16 h-16 bg-gradient-to-br from-red-500 to-red-600 rounded-2xl flex items-center justify-center mr-6 shadow-lg">
                    <AlertCircle className="w-8 h-8 text-white" />
                  </div>
                  <h3 className="text-3xl font-bold text-red-800">Immediate Danger</h3>
                </div>
                <p className="text-gray-700 mb-10 text-xl leading-relaxed">
                  If you are in immediate danger, please call <span className='font-bold text-red-600 text-2xl'>911</span> or go to your nearest police station right away.
                </p>
                <div className="flex space-x-6">
                  <button
                    onClick={() => setShowConfirmation(false)}
                    className="flex-1 bg-gradient-to-r from-red-600 to-red-700 text-white py-4 px-6 rounded-2xl text-xl font-bold hover:from-red-700 hover:to-red-800 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:-translate-y-1"
                  >
                    I'll call 911
                  </button>
                  <button
                    onClick={() => {
                      setShowConfirmation(false);
                      handleAnswer('no');
                    }}
                    className="flex-1 bg-gradient-to-r from-gray-200 to-gray-300 text-gray-700 py-4 px-6 rounded-2xl text-xl font-bold hover:from-gray-300 hover:to-gray-400 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:-translate-y-1"
                  >
                    Continue
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Header */}
          <div className="bg-gradient-to-br from-white to-blue-50 rounded-3xl shadow-2xl p-10 mb-10 border border-blue-100">
            <div className="flex items-center justify-between mb-10">
              <button
                onClick={handleBack}
                className="group flex items-center text-blue-700 hover:text-blue-900 transition-all duration-300 text-lg font-semibold bg-white px-6 py-3 rounded-2xl shadow-lg hover:shadow-xl transform hover:-translate-y-1"
              >
                <ChevronLeft className="w-5 h-5 mr-2 group-hover:-translate-x-1 transition-transform" />
                Back
              </button>
              <button
                onClick={() => navigate('/')} 
                className="group flex items-center text-blue-700 hover:text-blue-900 transition-all duration-300 text-lg font-semibold bg-white px-6 py-3 rounded-2xl shadow-lg hover:shadow-xl transform hover:-translate-y-1"
              >
                <Home className="w-5 h-5 mr-2 group-hover:scale-110 transition-transform" />
                Start Over
              </button>
            </div>
            <div className="flex items-center mb-10">
              <div className={`${currentTopic.color} w-24 h-24 rounded-3xl flex items-center justify-center text-white mr-8 shadow-2xl`}>
                <IconComponent className="w-12 h-12" />
              </div>
              <div>
                <h1 className="text-4xl md:text-5xl font-extrabold text-gray-900 mb-3 bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">{currentTopic.title}</h1>
                <div className="flex items-center space-x-4">
                  <p className="text-gray-600 text-xl font-medium">Question {currentStep + 1} of {currentTopic.questions.length}</p>
                  <div className="flex items-center space-x-2 text-blue-600">
                    <Clock className="w-5 h-5" />
                    <span className="text-sm font-medium">~{Math.ceil((currentTopic.questions.length - currentStep) * 2)} min remaining</span>
                  </div>
                </div>
              </div>
            </div>
            {/* Progress Bar */}
            <div className="w-full bg-gray-200 rounded-full h-6 shadow-inner">
              <div
                className="bg-gradient-to-r from-blue-500 to-purple-600 h-6 rounded-full transition-all duration-700 ease-out shadow-lg"
                style={{ width: `${getProgress()}%` }}
              />
            </div>
            <div className="mt-4 text-center">
              <span className="text-lg font-semibold text-gray-700">{Math.round(getProgress())}% Complete</span>
            </div>
          </div>

          {/* Question */}
          <div className="bg-gradient-to-br from-white to-gray-50 rounded-3xl shadow-2xl p-12 mb-10 border border-gray-100">
            <div className="mb-12">
              <h2 className="text-3xl md:text-4xl font-bold mb-6 text-gray-900 leading-tight">
                {currentQuestion.question}
              </h2>
              {currentQuestion.subtitle && (
                <p className="text-gray-600 mb-6 text-xl leading-relaxed">{currentQuestion.subtitle}</p>
              )}
              {currentQuestion.urgent && (
                <div className="bg-gradient-to-r from-red-50 to-pink-50 border-2 border-red-200 rounded-2xl p-6 mb-8">
                  <div className="flex items-center">
                    <div className="w-12 h-12 bg-gradient-to-br from-red-500 to-red-600 rounded-xl flex items-center justify-center mr-4">
                      <AlertCircle className="w-6 h-6 text-white" />
                    </div>
                    <span className="text-red-800 font-bold text-xl">Urgent: Safety First</span>
                  </div>
                </div>
              )}
            </div>
            <div className="space-y-6">
              {currentQuestion.options.map((option, index) => (
                <button
                  key={index}
                  onClick={() => handleAnswer(option.value)}
                  className="group w-full p-8 text-left border-2 border-gray-200 rounded-3xl hover:border-blue-500 hover:bg-gradient-to-r hover:from-blue-50 hover:to-indigo-50 transition-all duration-300 shadow-lg hover:shadow-2xl transform hover:-translate-y-2 focus:ring-4 focus:ring-blue-200 focus:outline-none"
                >
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <div className="font-bold text-gray-900 mb-3 group-hover:text-blue-700 text-xl transition-colors">
                        {option.label}
                      </div>
                      <div className="text-lg text-gray-600 leading-relaxed">
                        {option.description}
                      </div>
                    </div>
                    <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-blue-600 rounded-2xl flex items-center justify-center group-hover:scale-110 transition-transform duration-300 shadow-lg">
                      <ChevronRight className="w-6 h-6 text-white" />
                    </div>
                  </div>
                </button>
              ))}
            </div>
          </div>

          {/* Location Info (if available) */}
          {currentLocation && (
            <div className="bg-gradient-to-br from-blue-600 to-blue-700 rounded-3xl p-10 mt-10 shadow-2xl text-white">
              <div className="flex items-center mb-6">
                <div className="w-12 h-12 bg-white bg-opacity-20 rounded-2xl flex items-center justify-center mr-4">
                  <Scale className="w-6 h-6 text-white" />
                </div>
                <h3 className="font-bold text-2xl">Your Court Location</h3>
              </div>
              <p className="text-blue-100 text-xl">
                <span className="font-semibold text-white">{currentLocation.name}</span> • {currentLocation.phone}
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default QnA;
