import React, { useState, useEffect } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import { Search, Download, ExternalLink, FileText, Filter } from 'lucide-react';

const FormsPage = () => {
  const { language } = useLanguage();
  const [forms, setForms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');

  useEffect(() => {
    fetchForms();
  }, []);

  const fetchForms = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/forms');
      if (response.ok) {
        const data = await response.json();
        setForms(data);
      } else {
        console.error('Failed to fetch forms');
      }
    } catch (error) {
      console.error('Error fetching forms:', error);
    } finally {
      setLoading(false);
    }
  };

  const categories = [
    { id: 'all', name: { en: 'All Forms', es: 'Todos los Formularios' } },
    { id: 'family', name: { en: 'Family Law', es: 'Derecho de Familia' } },
    { id: 'civil', name: { en: 'Civil', es: 'Civil' } },
    { id: 'criminal', name: { en: 'Criminal', es: 'Penal' } },
    { id: 'probate', name: { en: 'Probate', es: 'Sucesiones' } },
    { id: 'small-claims', name: { en: 'Small Claims', es: 'Reclamos Menores' } }
  ];

  const filteredForms = forms.filter(form => {
    const matchesSearch = form.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (form.description && form.description.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesCategory = selectedCategory === 'all' || 
                          (form.required_for && form.required_for.toLowerCase().includes(selectedCategory));
    return matchesSearch && matchesCategory;
  });

  const handleDownload = (form) => {
    if (form.url_link) {
      window.open(form.url_link, '_blank');
    } else {
      alert(language === 'en' 
        ? 'Download link not available. Please contact court staff for assistance.'
        : 'Enlace de descarga no disponible. Por favor contacta al personal del tribunal para obtener ayuda.'
      );
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">
            {language === 'en' ? 'Loading forms...' : 'Cargando formularios...'}
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            {language === 'en' ? 'Court Forms' : 'Formularios Judiciales'}
          </h1>
          <p className="text-gray-600">
            {language === 'en' 
              ? 'Find and download the court forms you need. All forms are free to download and use.'
              : 'Encuentra y descarga los formularios judiciales que necesitas. Todos los formularios son gratuitos para descargar y usar.'
            }
          </p>
        </div>

        {/* Search and Filters */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {/* Search */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder={language === 'en' ? 'Search forms...' : 'Buscar formularios...'}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {/* Category Filter */}
            <div className="relative">
              <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 appearance-none bg-white"
              >
                {categories.map(category => (
                  <option key={category.id} value={category.id}>
                    {category.name[language]}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Forms Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredForms.length === 0 ? (
            <div className="col-span-full text-center py-12">
              <FileText className="w-16 h-16 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-gray-900 mb-2">
                {language === 'en' ? 'No forms found' : 'No se encontraron formularios'}
              </h3>
              <p className="text-gray-600">
                {language === 'en' 
                  ? 'Try adjusting your search terms or category filter.'
                  : 'Intenta ajustar los términos de búsqueda o el filtro de categoría.'
                }
              </p>
            </div>
          ) : (
            filteredForms.map((form, index) => (
              <div key={index} className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow">
                <div className="flex items-start justify-between mb-4">
                  <div className="flex-1">
                    <h3 className="text-lg font-semibold text-gray-900 mb-2">
                      {form.title}
                    </h3>
                    {form.description && (
                      <p className="text-gray-600 text-sm mb-3">
                        {form.description}
                      </p>
                    )}
                    {form.required_for && (
                      <div className="inline-block bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded-full">
                        {form.required_for}
                      </div>
                    )}
                  </div>
                </div>

                <div className="flex space-x-2">
                  <button
                    onClick={() => handleDownload(form)}
                    className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors flex items-center justify-center"
                  >
                    <Download className="w-4 h-4 mr-2" />
                    {language === 'en' ? 'Download' : 'Descargar'}
                  </button>
                  {form.url_link && (
                    <button
                      onClick={() => window.open(form.url_link, '_blank')}
                      className="px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
                      title={language === 'en' ? 'Open in new tab' : 'Abrir en nueva pestaña'}
                    >
                      <ExternalLink className="w-4 h-4" />
                    </button>
                  )}
                </div>
              </div>
            ))
          )}
        </div>

        {/* Quick Links */}
        <div className="mt-8 bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">
            {language === 'en' ? 'Quick Links' : 'Enlaces Rápidos'}
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            <a
              href="https://www.courts.ca.gov/forms.htm"
              target="_blank"
              rel="noopener noreferrer"
              className="flex items-center p-3 border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors"
            >
              <FileText className="w-5 h-5 text-blue-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">
                  {language === 'en' ? 'California Courts Forms' : 'Formularios de Tribunales de California'}
                </p>
                <p className="text-sm text-gray-600">
                  {language === 'en' ? 'Official state forms' : 'Formularios oficiales del estado'}
                </p>
              </div>
            </a>
            <a
              href="https://www.sanmateocourt.org/self_help"
              target="_blank"
              rel="noopener noreferrer"
              className="flex items-center p-3 border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors"
            >
              <FileText className="w-5 h-5 text-blue-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">
                  {language === 'en' ? 'San Mateo Self-Help' : 'Autoayuda de San Mateo'}
                </p>
                <p className="text-sm text-gray-600">
                  {language === 'en' ? 'Local court resources' : 'Recursos del tribunal local'}
                </p>
              </div>
            </a>
            <a
              href="https://www.courts.ca.gov/selfhelp.htm"
              target="_blank"
              rel="noopener noreferrer"
              className="flex items-center p-3 border border-gray-200 rounded-lg hover:bg-gray-50 transition-colors"
            >
              <FileText className="w-5 h-5 text-blue-600 mr-3" />
              <div>
                <p className="font-medium text-gray-900">
                  {language === 'en' ? 'Self-Help Center' : 'Centro de Autoayuda'}
                </p>
                <p className="text-sm text-gray-600">
                  {language === 'en' ? 'Free legal assistance' : 'Asistencia legal gratuita'}
                </p>
              </div>
            </a>
          </div>
        </div>

        {/* Important Notice */}
        <div className="mt-8 bg-yellow-50 border border-yellow-200 rounded-lg p-6">
          <h3 className="text-lg font-semibold text-yellow-900 mb-2">
            {language === 'en' ? 'Important Notice' : 'Aviso Importante'}
          </h3>
          <p className="text-yellow-800">
            {language === 'en' 
              ? 'These forms are provided for informational purposes only. Completing forms does not guarantee a specific outcome. For complex legal matters, consider consulting with an attorney. Court staff cannot provide legal advice.'
              : 'Estos formularios se proporcionan únicamente con fines informativos. Completar formularios no garantiza un resultado específico. Para asuntos legales complejos, considere consultar con un abogado. El personal del tribunal no puede proporcionar asesoramiento legal.'
            }
          </p>
        </div>
      </div>
    </div>
  );
};

export default FormsPage;
