import React, { useState } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import { 
  Shield, 
  Heart, 
  FileText, 
  Users, 
  Globe,
  ArrowRight,
  Clock,
  CheckCircle,
  FileText as FileTextIcon
} from 'lucide-react';
import ProcessGuidance from '../components/ProcessGuidance';

const UserKiosk = () => {
  const { language, toggleLanguage } = useLanguage();
  const [selectedCase, setSelectedCase] = useState(null);
  const [queueNumber, setQueueNumber] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [showProcessGuidance, setShowProcessGuidance] = useState(false);
  const [caseSummary, setCaseSummary] = useState('');
  const [nextSteps, setNextSteps] = useState('');

  const caseTypes = [
    {
      id: 'A',
      priority: 'A',
      title: { en: 'Domestic Violence', es: 'Violencia Doméstica' },
      description: { 
        en: 'Restraining orders, protection orders, emergency cases', 
        es: 'Órdenes de restricción, órdenes de protección, casos de emergencia' 
      },
      icon: Shield,
      color: 'bg-red-500',
      borderColor: 'border-red-500'
    },
    {
      id: 'B',
      priority: 'B',
      title: { en: 'Child Custody & Support', es: 'Custodia y Manutención' },
      description: { 
        en: 'Child custody, child support, visitation rights', 
        es: 'Custodia de menores, manutención infantil, derechos de visita' 
      },
      icon: Heart,
      color: 'bg-orange-500',
      borderColor: 'border-orange-500'
    },
    {
      id: 'C',
      priority: 'C',
      title: { en: 'Divorce & Separation', es: 'Divorcio y Separación' },
      description: { 
        en: 'Divorce proceedings, legal separation, property division', 
        es: 'Procedimientos de divorcio, separación legal, división de bienes' 
      },
      icon: FileText,
      color: 'bg-yellow-500',
      borderColor: 'border-yellow-500'
    },
    {
      id: 'D',
      priority: 'D',
      title: { en: 'Other Family Law', es: 'Otro Derecho de Familia' },
      description: { 
        en: 'Adoption, guardianship, name changes, other family matters', 
        es: 'Adopción, tutela, cambios de nombre, otros asuntos familiares' 
      },
      icon: Users,
      color: 'bg-blue-500',
      borderColor: 'border-blue-500'
    }
  ];

  const handleCaseSelection = async (caseType) => {
    setSelectedCase(caseType);
    setIsProcessing(true);

    try {
      const response = await fetch('http://localhost:5001/api/generate-queue', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          case_type: caseType.id,
          priority: caseType.priority,
          language: language
        })
      });

      if (response.ok) {
        const data = await response.json();
        setQueueNumber(data.queue_number);
        setShowProcessGuidance(true);
      } else {
        console.error('Failed to generate queue number');
      }
    } catch (error) {
      console.error('Error generating queue number:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  const handleProcessComplete = (summary, steps) => {
    setCaseSummary(summary);
    setNextSteps(steps);
    setShowProcessGuidance(false);
  };

  const resetKiosk = () => {
    setSelectedCase(null);
    setQueueNumber(null);
    setShowProcessGuidance(false);
    setCaseSummary('');
    setNextSteps('');
  };

  // Show process guidance
  if (showProcessGuidance && queueNumber) {
    return (
      <ProcessGuidance
        caseType={selectedCase.id}
        queueNumber={queueNumber}
        onComplete={handleProcessComplete}
      />
    );
  }

  // Show final queue number with summary
  if (queueNumber && !showProcessGuidance) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center p-4">
        <div className="bg-white rounded-lg shadow-xl p-8 max-w-2xl w-full text-center">
          <CheckCircle className="w-16 h-16 text-green-500 mx-auto mb-6" />
          <h1 className="text-3xl font-bold text-gray-900 mb-4">
            {language === 'en' ? 'Your Queue Number' : 'Tu Número de Cola'}
          </h1>

          <div className={`text-6xl font-bold mb-6 p-6 rounded-lg ${
            selectedCase?.color || 'bg-gray-500'
          } text-white`}>
            {queueNumber}
          </div>

          <div className="mb-6">
            <h2 className="text-xl font-semibold text-gray-900 mb-2">
              {selectedCase?.title[language]}
            </h2>
            <p className="text-gray-600">
              {selectedCase?.description[language]}
            </p>
          </div>

          {caseSummary && (
            <div className="mb-6 bg-blue-50 border border-blue-200 rounded-lg p-4 text-left">
              <h3 className="font-semibold text-blue-900 mb-2">
                {language === 'en' ? 'Case Summary' : 'Resumen del Caso'}
              </h3>
              <div className="text-blue-800 text-sm whitespace-pre-line">
                {caseSummary}
              </div>
            </div>
          )}

          {nextSteps && (
            <div className="mb-6 bg-green-50 border border-green-200 rounded-lg p-4 text-left">
              <h3 className="font-semibold text-green-900 mb-2 flex items-center">
                <FileTextIcon className="w-4 h-4 mr-2" />
                {language === 'en' ? 'Next Steps' : 'Próximos Pasos'}
              </h3>
              <div className="text-green-800 text-sm whitespace-pre-line">
                {nextSteps}
              </div>
            </div>
          )}

          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 mb-6">
            <div className="flex items-center justify-center mb-2">
              <Clock className="w-5 h-5 text-yellow-600 mr-2" />
              <span className="text-yellow-800 font-semibold">
                {language === 'en' ? 'Please wait to be called' : 'Por favor espera a ser llamado'}
              </span>
            </div>
            <p className="text-yellow-700 text-sm">
              {language === 'en' 
                ? 'A facilitator will assist you shortly. Your information has been sent to the facilitator office.'
                : 'Un facilitador te ayudará pronto. Tu información ha sido enviada a la oficina del facilitador.'
              }
            </p>
          </div>

          <button
            onClick={resetKiosk}
            className="w-full bg-gray-600 text-white px-6 py-3 rounded-lg hover:bg-gray-700 transition-colors"
          >
            {language === 'en' ? 'Start Over' : 'Comenzar de Nuevo'}
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-lg border-b">
        <div className="max-w-4xl mx-auto px-4 py-6">
          <div className="flex justify-between items-center">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">
                {language === 'en' ? 'Family Court Clinic' : 'Clínica del Tribunal de Familia'}
              </h1>
              <p className="text-gray-600 mt-1">
                {language === 'en' 
                  ? 'Select your case type to get guidance and a queue number'
                  : 'Selecciona tu tipo de caso para obtener orientación y un número de cola'
                }
              </p>
            </div>
            <button
              onClick={toggleLanguage}
              className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              <Globe className="w-4 h-4 mr-2" />
              {language === 'en' ? 'Español' : 'English'}
            </button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-4xl mx-auto px-4 py-8">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {caseTypes.map((caseType) => {
            const Icon = caseType.icon;
            return (
              <button
                key={caseType.id}
                onClick={() => handleCaseSelection(caseType)}
                disabled={isProcessing}
                className={`w-full p-6 border-2 rounded-lg hover:shadow-lg transition-all duration-200 text-left ${
                  caseType.borderColor
                } hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed`}
              >
                <div className="flex items-center mb-4">
                  <div className={`w-12 h-12 ${caseType.color} rounded-lg flex items-center justify-center mr-4`}>
                    <Icon className="w-6 h-6 text-white" />
                  </div>
                  <div>
                    <h3 className="text-xl font-semibold text-gray-900">
                      {caseType.title[language]}
                    </h3>
                    <div className={`inline-block px-3 py-1 rounded-full text-white text-sm font-bold ${
                      caseType.color
                    }`}>
                      Priority {caseType.priority}
                    </div>
                  </div>
                </div>
                <p className="text-gray-600 mb-4">
                  {caseType.description[language]}
                </p>
                <div className="flex items-center text-blue-600 font-medium">
                  <span>
                    {language === 'en' ? 'Select this case type' : 'Seleccionar este tipo de caso'}
                  </span>
                  <ArrowRight className="w-4 h-4 ml-2" />
                </div>
              </button>
            );
          })}
        </div>

        {/* Instructions */}
        <div className="mt-8 bg-blue-50 border border-blue-200 rounded-lg p-6">
          <h2 className="text-lg font-semibold text-blue-900 mb-3">
            {language === 'en' ? 'How it works' : 'Cómo funciona'}
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm text-blue-800">
            <div className="flex items-start">
              <div className="w-6 h-6 bg-blue-600 text-white rounded-full flex items-center justify-center text-xs font-bold mr-3 mt-0.5">
                1
              </div>
              <div>
                <p className="font-semibold mb-1">
                  {language === 'en' ? 'Select Case Type' : 'Selecciona Tipo de Caso'}
                </p>
                <p>
                  {language === 'en' 
                    ? 'Choose the category that best describes your situation'
                    : 'Elige la categoría que mejor describe tu situación'
                  }
                </p>
              </div>
            </div>
            <div className="flex items-start">
              <div className="w-6 h-6 bg-blue-600 text-white rounded-full flex items-center justify-center text-xs font-bold mr-3 mt-0.5">
                2
              </div>
              <div>
                <p className="font-semibold mb-1">
                  {language === 'en' ? 'Get Process Guidance' : 'Obtén Orientación del Proceso'}
                </p>
                <p>
                  {language === 'en' 
                    ? 'Answer questions to determine where you are in your case'
                    : 'Responde preguntas para determinar dónde estás en tu caso'
                  }
                </p>
              </div>
            </div>
            <div className="flex items-start">
              <div className="w-6 h-6 bg-blue-600 text-white rounded-full flex items-center justify-center text-xs font-bold mr-3 mt-0.5">
                3
              </div>
              <div>
                <p className="font-semibold mb-1">
                  {language === 'en' ? 'Get Queue Number' : 'Obtén Número de Cola'}
                </p>
                <p>
                  {language === 'en' 
                    ? 'Receive your priority queue number and wait for assistance'
                    : 'Recibe tu número de cola prioritario y espera asistencia'
                  }
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Emergency Notice */}
        <div className="mt-6 bg-red-50 border border-red-200 rounded-lg p-4">
          <div className="flex items-start">
            <Shield className="w-5 h-5 text-red-600 mr-3 mt-0.5" />
            <div>
              <h3 className="text-red-900 font-semibold mb-1">
                {language === 'en' ? 'Emergency Information' : 'Información de Emergencia'}
              </h3>
              <p className="text-red-800 text-sm">
                {language === 'en' 
                  ? 'If you are in immediate danger, call 911. For domestic violence assistance, call 1-800-799-7233.'
                  : 'Si estás en peligro inmediato, llama al 911. Para asistencia de violencia doméstica, llama al 1-800-799-7233.'
                }
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserKiosk;
