import React, { useState, useEffect } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import { Search, Phone, Mail, Clock, User, Building } from 'lucide-react';

const StaffPage = () => {
  const { language } = useLanguage();
  const [staff, setStaff] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedRole, setSelectedRole] = useState('all');

  useEffect(() => {
    fetchStaff();
  }, []);

  const fetchStaff = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/staff');
      if (response.ok) {
        const data = await response.json();
        setStaff(data);
      } else {
        console.error('Failed to fetch staff');
      }
    } catch (error) {
      console.error('Error fetching staff:', error);
    } finally {
      setLoading(false);
    }
  };

  const roles = [
    { id: 'all', name: { en: 'All Staff', es: 'Todo el Personal' } },
    { id: 'self-help', name: { en: 'Self-Help Center', es: 'Centro de Autoayuda' } },
    { id: 'clerk', name: { en: 'Court Clerk', es: 'Secretario del Tribunal' } },
    { id: 'judge', name: { en: 'Judge', es: 'Juez' } },
    { id: 'mediator', name: { en: 'Mediator', es: 'Mediador' } },
    { id: 'administrator', name: { en: 'Administrator', es: 'Administrador' } }
  ];

  const filteredStaff = staff.filter(member => {
    const matchesSearch = member.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (member.role && member.role.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesRole = selectedRole === 'all' || 
                       (member.role && member.role.toLowerCase().includes(selectedRole));
    return matchesSearch && matchesRole;
  });

  const handleContact = (type, value) => {
    if (type === 'phone' && value) {
      window.open(`tel:${value}`, '_self');
    } else if (type === 'email' && value) {
      window.open(`mailto:${value}`, '_self');
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">
            {language === 'en' ? 'Loading staff directory...' : 'Cargando directorio del personal...'}
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            {language === 'en' ? 'Court Staff Directory' : 'Directorio del Personal Judicial'}
          </h1>
          <p className="text-gray-600">
            {language === 'en' 
              ? 'Contact information for court staff and departments. Office hours may vary.'
              : 'Información de contacto del personal judicial y departamentos. Los horarios de oficina pueden variar.'
            }
          </p>
        </div>

        {/* Search and Filters */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {/* Search */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder={language === 'en' ? 'Search staff...' : 'Buscar personal...'}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {/* Role Filter */}
            <div className="relative">
              <User className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <select
                value={selectedRole}
                onChange={(e) => setSelectedRole(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 appearance-none bg-white"
              >
                {roles.map(role => (
                  <option key={role.id} value={role.id}>
                    {role.name[language]}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Staff Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredStaff.length === 0 ? (
            <div className="col-span-full text-center py-12">
              <User className="w-16 h-16 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-gray-900 mb-2">
                {language === 'en' ? 'No staff found' : 'No se encontró personal'}
              </h3>
              <p className="text-gray-600">
                {language === 'en' 
                  ? 'Try adjusting your search terms or role filter.'
                  : 'Intenta ajustar los términos de búsqueda o el filtro de rol.'
                }
              </p>
            </div>
          ) : (
            filteredStaff.map((member, index) => (
              <div key={index} className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow">
                <div className="flex items-start justify-between mb-4">
                  <div className="flex-1">
                    <h3 className="text-lg font-semibold text-gray-900 mb-1">
                      {member.name}
                    </h3>
                    {member.role && (
                      <p className="text-blue-600 font-medium mb-2">
                        {member.role}
                      </p>
                    )}
                  </div>
                </div>

                <div className="space-y-3">
                  {member.phone && (
                    <button
                      onClick={() => handleContact('phone', member.phone)}
                      className="flex items-center w-full text-left text-gray-700 hover:text-blue-600 transition-colors"
                    >
                      <Phone className="w-4 h-4 mr-3 text-gray-500" />
                      <span className="text-sm">{member.phone}</span>
                    </button>
                  )}

                  {member.email && (
                    <button
                      onClick={() => handleContact('email', member.email)}
                      className="flex items-center w-full text-left text-gray-700 hover:text-blue-600 transition-colors"
                    >
                      <Mail className="w-4 h-4 mr-3 text-gray-500" />
                      <span className="text-sm break-all">{member.email}</span>
                    </button>
                  )}

                  {member.office_hours && (
                    <div className="flex items-start">
                      <Clock className="w-4 h-4 mr-3 text-gray-500 mt-0.5" />
                      <div className="text-sm text-gray-600">
                        <p className="font-medium text-gray-700">
                          {language === 'en' ? 'Office Hours' : 'Horario de Oficina'}
                        </p>
                        <p>{member.office_hours}</p>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            ))
          )}
        </div>

        {/* General Contact Information */}
        <div className="mt-8 bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold text-gray-900 mb-4">
            {language === 'en' ? 'General Contact Information' : 'Información de Contacto General'}
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-4">
              <div className="flex items-center">
                <Building className="w-5 h-5 text-gray-600 mr-3" />
                <div>
                  <p className="font-semibold text-gray-900">
                    {language === 'en' ? 'Main Courthouse' : 'Tribunal Principal'}
                  </p>
                  <p className="text-gray-600">400 County Center, Redwood City, CA 94063</p>
                </div>
              </div>
              <div className="flex items-center">
                <Phone className="w-5 h-5 text-gray-600 mr-3" />
                <div>
                  <p className="font-semibold text-gray-900">
                    {language === 'en' ? 'Main Phone' : 'Teléfono Principal'}
                  </p>
                  <p className="text-gray-600">650-261-5060</p>
                </div>
              </div>
            </div>
            <div className="space-y-4">
              <div className="flex items-center">
                <Clock className="w-5 h-5 text-gray-600 mr-3" />
                <div>
                  <p className="font-semibold text-gray-900">
                    {language === 'en' ? 'Business Hours' : 'Horario de Atención'}
                  </p>
                  <p className="text-gray-600">
                    {language === 'en' ? 'Monday - Friday, 8:00 AM - 4:00 PM' : 'Lunes - Viernes, 8:00 AM - 4:00 PM'}
                  </p>
                </div>
              </div>
              <div className="flex items-center">
                <Mail className="w-5 h-5 text-gray-600 mr-3" />
                <div>
                  <p className="font-semibold text-gray-900">
                    {language === 'en' ? 'General Email' : 'Correo General'}
                  </p>
                  <p className="text-gray-600">info@sanmateocourt.org</p>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Emergency Contacts */}
        <div className="mt-8 bg-red-50 border border-red-200 rounded-lg p-6">
          <h2 className="text-xl font-semibold text-red-900 mb-4">
            {language === 'en' ? 'Emergency Contacts' : 'Contactos de Emergencia'}
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="bg-white p-4 rounded-lg">
              <h3 className="font-semibold text-gray-900 mb-2">
                {language === 'en' ? 'Domestic Violence' : 'Violencia Doméstica'}
              </h3>
              <p className="text-red-600 font-bold">1-800-799-7233</p>
              <p className="text-sm text-gray-600">
                {language === 'en' ? '24/7 confidential support' : 'Apoyo confidencial 24/7'}
              </p>
            </div>
            <div className="bg-white p-4 rounded-lg">
              <h3 className="font-semibold text-gray-900 mb-2">
                {language === 'en' ? 'Emergency Services' : 'Servicios de Emergencia'}
              </h3>
              <p className="text-red-600 font-bold">911</p>
              <p className="text-sm text-gray-600">
                {language === 'en' ? 'For immediate danger' : 'Para peligro inmediato'}
              </p>
            </div>
          </div>
        </div>

        {/* Important Notice */}
        <div className="mt-8 bg-yellow-50 border border-yellow-200 rounded-lg p-6">
          <h3 className="text-lg font-semibold text-yellow-900 mb-2">
            {language === 'en' ? 'Important Notice' : 'Aviso Importante'}
          </h3>
          <p className="text-yellow-800">
            {language === 'en' 
              ? 'Court staff cannot provide legal advice. For legal advice, please consult with an attorney. This directory is for informational purposes only.'
              : 'El personal del tribunal no puede proporcionar asesoramiento legal. Para asesoramiento legal, por favor consulte con un abogado. Este directorio es solo para fines informativos.'
            }
          </p>
        </div>
      </div>
    </div>
  );
};

export default StaffPage;
